import { Component } from "@angular/core";
import { ActivatedRoute, ParamMap } from "@angular/router";
import { ProductService } from "../../product.service";
import { Hotel } from "src/app/model/product";
import { of, switchMap } from "rxjs";
import { HttpClient } from "@angular/common/http";
import emailjs from "emailjs-com";

@Component({
  selector: "app-product-detail",
  templateUrl: "./product-detail.component.html",
  styleUrls: ["./product-detail.component.css"],
})
export class HotelDetailComponent {
  hotel: Hotel | null = null;
  showForm: boolean = false;
  formData = {
    id: 0,
    name: "",
    type: "",
    location: "",
    price: 0,
    rating: 0,
    hasLounge: false,
    hasParking: false,
  };
  constructor(
    private activedRoute: ActivatedRoute,
    private productSvc: ProductService,
    private http: HttpClient
  ) {}

  ngOnInit() {
    this.activedRoute.paramMap
      .pipe(
        switchMap((paramMap: ParamMap) => {
          const hotelId = paramMap.get("hotelId");

          if (hotelId) {
            console.log(hotelId);
            return this.productSvc.getProduct(hotelId);
          }
          return of(null);
        })
      )
      .subscribe({
        next: (hotel) => {
          this.hotel = hotel;
        },
      });
  }
  BookNow(item: any) {
    if (this.showForm === false) {
      this.showForm = true;
    } else {
      this.showForm = false;
    }
  }
  sendEmail() {
    this.showForm = false;

    emailjs
      .send(
        "service_a6f8v18",
        "template_s1lc4fh",
        {
          to_email: "babumishra7898@gmail.com", // Replace with recipient's email
          message: "Hello, this is a test email sent from Angular!",
        },
        "E3LKiGMaqM7wCO6xC"
      )
      .then(
        (response) => {
          console.log("Email sent successfully:", response);
          // Handle success, e.g., show a success message to the user
        },
        (error) => {
          console.error("Email sending error:", error);
          // Handle error, e.g., show an error message to the user
        }
      );
  }

  showData() {
    console.log(this.formData);
    this.productSvc.getViewBookingData(this.formData);
    this.showForm = false;
  }
}
<div id="details_id" *ngIf="!showForm">
  <div class="product-info" *ngIf="hotel">
    <h2>{{ hotel.name }}</h2>
    <p>
      <img
        src="https://wallpapershome.com/images/pages/ico_h/19258.jpg"
        id="product-image"
      />
    </p>
    <p class="type">Type:{{ hotel.type }}</p>
    <p class="type">Rating:{{ hotel.rating }}</p>
    <p class="product-price">Price: {{ hotel.price }}</p>
    <button (click)="BookNow(hotel)" id="btn">Book Now</button>
  </div>
</div>
<div class="myform1" *ngIf="showForm">
  <form #myForm="ngForm" class="form-container">
    <div class="form-group">
      <label for="name">id</label>

      <input
        type="number"
        class="form-control"
        name="id"
        [(ngModel)]="formData.id"
        required
      />
    </div>
    <div class="form-group">
      <label for="name">Name</label>

      <input
        type="text"
        class="form-control"
        name="name"
        [(ngModel)]="formData.name"
        required
      />
    </div>

    <div class="form-group">
      <label for="descriotion">Type of Room</label>

      <label>
        <input
          type="radio"
          name="roomType"
          value="ac"
          [(ngModel)]="formData.type"
        />
        Air Conditioned (AC) Room
      </label>
      <br />
      <label>
        <input
          type="radio"
          name="roomType"
          value="non-ac"
          [(ngModel)]="formData.type"
        />
        Non-Air Conditioned (Non-AC) Room
      </label>
    </div>

    <div class="form-group">
      <label for="name">Price</label>

      <input
        type="text"
        class="form-control"
        name="price"
        [(ngModel)]="formData.price"
        required
      />
    </div>

    <div class="form-group">
      <label for="name">Location</label>

      <input
        type="text"
        class="form-control"
        name="location"
        [(ngModel)]="formData.location"
        required
      />
    </div>

    <button type="submit" class="btn btn-primary" (click)="showData()">
      Add Hotel
    </button>
  </form>
</div>
