@Component({
  selector: "app-header",
  templateUrl: "./header.component.html",
  styleUrls: ["./header.component.css"],
})
export class HeaderComponent {}
<div class="header">
  HotelMart
  <div class="links">
    <li><a href="/home">Home</a></li>
    <li><a href="/add">Add Hotel</a></li>
    <li><a href="/hotel">All Hotels</a></li>
  </div>
</div>
