import { Component, OnInit } from "@angular/core";
import { Hotel } from "src/app/model/product";
import { ProductService } from "../../product.service";

@Component({
  selector: "app-product-list",
  templateUrl: "./product-list.component.html",
  styleUrls: ["./product-list.component.css"],
})
export class HotelListComponent implements OnInit {
  hotels: Hotel[] = [];
  filteredHotels: Hotel[] = [];
  filters: any = {};
  minPrice = 0;
  maxPrice = 0;
  maxRating = 0;
  minRating = 0;
  hasLounge = false;
  hasParking = false;
  location = "";
  type: any;

  constructor(private productService: ProductService) {}
  // apiData: ApiResponse | null = null;
  isLoading = true;

  ngOnInit(): void {
    this.hotels = this.productService.getProducts();
    this.productService.getApiData().subscribe((hotels) => {
      this.hotels = hotels;
      this.filteredHotels = hotels;
    });
  }

  getFilteredHotels(hotel: any): void {
    console.log(this.filteredHotels);
    this.filteredHotels = this.hotels.filter(
      (hotel) =>
        hotel.price >= this.minPrice &&
        hotel.price <= this.maxPrice &&
        hotel.rating >= this.minRating &&
        hotel.rating <= this.maxRating &&
        hotel.type === this.type &&
        (this.location === "" ||
          hotel.location.toLowerCase().includes(this.location.toLowerCase()))
    );
  }
}
<div class="product_filter">
  <!-- Type of Hotel -->
  <h1>Filter Window</h1>
  <select ng-model="selectedType">
    <option value="">All Types</option>
    <option value="Non-AC">Non-AC</option>
    <option value="AC">AC</option>
    <!-- Add more options as needed -->
  </select>
  <div class="input1">
    <!-- Price Range -->
    <input type="number" [(ngModel)]="minPrice" placeholder="Min Price" />

    <input type="number" [(ngModel)]="maxPrice" placeholder="Max Price" />

    <!-- Rating Range -->
    <input type="number" [(ngModel)]="minRating" placeholder="Min Rating" />

    <input type="number" [(ngModel)]="maxRating" placeholder="Max Rating" />

    <!-- Location -->
    <input type="text" [(ngModel)]="location" placeholder="Location" />

    <!-- Has Lounge -->
    <input type="checkbox" [(ngModel)]="hasLounge" /> Has Lounge

    <!-- Has Parking -->
    <input type="checkbox" [(ngModel)]="hasParking" /> Has Parking

    <button
      type="submit"
      class="primary-btn"
      (click)="getFilteredHotels(hotels)"
    >
      Apply Filters
    </button>
  </div>
</div>

<div class="product-list">
  <h1>All Hotels</h1>
  <div *ngFor="let hotel of filteredHotels">
    <h3 id="name">Name: {{ hotel.name }}</h3>
    <p>Location:{{ hotel.location }}</p>
    <p>Type:{{ hotel.type }}</p>
    <div class="alldetails">
      <!-- <button (clcik)="onDeleteCart(product)">Delete from Cart</button> -->
      <p id="price">Price: {{ hotel.price }}</p>
      <p id="rating">Rating:{{ hotel.rating }}</p>
      <button id="show_details">
        <a [routerLink]="['/hotel', hotel.id]">Show Details</a>
      </button>
    </div>
  </div>
</div>
