import { Component, OnInit } from '@angular/core';
import {Product } from 'src/app/model/product';
import { ProductService } from '../../product.service';
import { CartService } from 'src/app/cart/cart.service';

@Component({
  selector: 'app-product-list',
  templateUrl: './product-list.component.html',
  styleUrls: ['./product-list.component.css']
})
export class ProductListComponent implements OnInit{
  products: Product[] = [];
  filteredProduct:Product[]=[];
  hotels:any[]=[];
  filters:any={};
  minPrice: number | undefined = undefined;
maxPrice:number | undefined,
maxRating=0;
minRating=0;
hasLounge=true;
hasParking=true;
location='';



  constructor(
    private productService: ProductService,
    private cartService: CartService) { }
    // apiData: ApiResponse | null = null;
    isLoading = true;

  ngOnInit(): void {
    this.products = this.productService.getProducts();
    this.productService.getApiData().subscribe(
      (products)=>{
        this.products= products
        this.filteredProduct=products;
      }
    );
    // this.filteredProduct=[...this.products];

    // this.getFilteredHotels(this.products);
  }
  onAddToCart(product: Product): void {
    this.cartService.addToCart(product);
  }

  getFilteredHotels(product:any):void {
    // this.productService.getFilteredHotels(this.filters).subscribe(
    //   (data: any[]) => {
    //     console.log("hello")
    //     this.hotels = data;
    //     console.log(this.minPrice);
    //   },
    //   (error: any) => {
    //     console.error('Error fetching hotels', error);
    //   }
    // );
    console.log(this.filteredProduct);
    this.filteredProduct = this.products.filter(product=>product.price>=this.minPrice&&
      product.price <= this.maxPrice&&product.rating>=this.minRating&&product.rating<=this.maxRating&& 
      (this.location === '' || product.location.toLowerCase().includes(this.location.toLowerCase()))

    );
  }



  }
